$a = $text | ConvertFrom-Json $a | Add-Member -Type NoteProperty -Name 'commandscreenshot' -Value $irfvpath' /capture=1 /convert=' $a | Add-Member -Type NoteProperty -Name 'featurepath' -Value '%env.VA.FeaturePath%' $a | Add-Member -Type NoteProperty -Name 'outputscreenshot' -Value '%system.teamcity.build.tempDir%' $a | Add-Member -Type NoteProperty -Name 'texterrorslogname' -Value '%system.teamcity.build.tempDir%' $a | Add-Member -Type NoteProperty -Name 'textlogname' -Value '%system.teamcity.build.tempDir%/test.txt' $a.AllureReport | Add-Member -Type NoteProperty -Name 'directorytobuildhierarchy' -Value '%env.VA.FeaturePath%' $a.librarycatalogs += '%system.teamcity.build.workingDir%\DataProcessor' $addTags = %env.FilterTagsForVA%; If ($addTags -eq '') {
}else { $a.filtertags += $addTags }
$b = $a | ConvertTo-Json -depth 32 $b | set-content %system.teamcity.build.workingDir%/va.json Write-Output "##teamcity[compilationStarted compiler='VA settings']" Write-Output "$b" Write-Output "##teamcity[compilationFinished compiler='VA settings']"

Write-Host "##teamcity[progressStart 'Read VA steps']"
$processID = %env.VA_PID%
$filePath = "%system.teamcity.build.tempDir%/test.txt"
while (!(Test-Path $filePath)) { Start-Sleep 1 }

    $loopBlock = {
        Param($filePath) Get-Content $filePath -Wait -Tail 0 -Encoding UTF8
    }
    $TailLoopJob = start-job -scriptBlock $loopBlock -ArgumentList $filePath
    try {
        do {
            $TailLoopJob | Receive-Job
            If(Get-Process -id $processID -ErrorAction SilentlyContinue ){
                Start-Sleep 1                                
                $waitMore = $true
            } else {
                $waitMore = $false
            }
        } while($waitMore)
    } finally {
        Stop-Job $TailLoopJob
        Remove-Job $TailLoopJob
    }
Write-Host "##teamcity[progressStart 'Read VA steps']"
